/****** Object:  UserDefinedFunction [dbo].[GenerateCustomTID]    Script Date: 25-04-2024 17:52:25 ******/
SET ANSI_NULLS ON
GO
SET QUOTED_IDENTIFIER ON
GO

Create FUNCTION [dbo].[GenerateCustomTID]()
RETURNS INT
AS
BEGIN
    DECLARE @TID INT
    DECLARE @DatePart VARCHAR(8)
    SET @DatePart = CONVERT(VARCHAR(8), GETDATE(), 112)
    DECLARE @LastTID INT
    SELECT @LastTID = MAX(TID) FROM PgTransaction
    WHERE LEFT(CONVERT(VARCHAR(10), GETDATE(), 112), 8) = @DatePart
    SET @TID = COALESCE(@LastTID, 0) + 1
    WHILE @TID < 100000
    BEGIN
        SET @TID = @TID * 10
    END
    RETURN @TID
END

