    END
    ELSE
    BEGIN
        IF EXISTS(SELECT 1 FROM Medicines WHERE BarCode = @BarCode AND PharmacyId = @PharmacyId AND Id <> @Id)
        BEGIN
            SELECT 0 AS success, 'Medicine already exists.' AS [Message]
            RETURN
        END

        UPDATE Medicines
        SET WID = @WID,
            HospitalId = @HospitalId,
            PharmacyId = @PharmacyId,
            BarCode = @BarCode,
            SupplierId = @SupplierId,
