            Shelf = @Shelf,
            Details = @Details,
            Price = @Price,
            ImageUrl = @ImageUrl,
            SupplierPrice = @SupplierPrice,
            TaxInPercentage = @TaxInPercentage,
            UpdateAt = GETDATE(),
            UpdateBy = @PharmacyId
        WHERE Id = @Id;

        SELECT 1 AS success, 'Medicine updated successfully.' AS [Message]
    END
END
