CREATE Proc [dbo].[Proc_AdmitPatient]
@PatientUserName varchar(50),
@DOB VARCHAR(50),
@Age int,
@BloodGroup varchar(20),
@MaritalStatus varchar(10),
@Gender varchar(5),
@GuardianName varchar(50),
@GuardianMobile varchar(50),
@RelationWithPatient varchar(150),
@BloodPressure varchar(50),
@PulseRate varchar(50),
@Wight varchar(50),
@WardType int,
@RoomId int,
@BedId int,
@DoctorId int,
@PrescriptionFile varchar(50),
@Diagnosis nvarchar(max),
@Prescription nvarchar(max),
@loginId int,
@HospitalId int,
@WID int,
@ReferedType VARCHAR(5),
@ReferedBY INT,
@PreferredForAdmit BIT
AS
BEGIN
	DECLARE @UserId INT,@PatientId int

	SELECT @UserId = Id FROM Users WHERE UserName = @PatientUserName
	IF EXISTS(SELECT 1 FROM Patients WHERE UserId = @UserId AND ISNULL(IsAdmited,0) = 1 AND ISNULL(IsRelease,0) = 0)
	BEGIN
		SELECT 0 Success,'Patient Already Admited.' [Message]
		return
	END

	BEGIN TRY
		BEGIN TRAN

		IF EXISTS(SELECT 1 FROM Patients WHERE UserId = @UserId)
		BEGIN
			UPDATE Patients SET BloodGroup = @BloodGroup,Age = @Age,ReferedType = @ReferedType,GuardianName = @GuardianName,GuardianMobile = @GuardianMobile,Relation_with_Patient = @RelationWithPatient,Marital = @MaritalStatus,IsAdmited = @PreferredForAdmit,@PatientId = Id,
			 AdmissionDate = GETDATE()
			WHERE UserId = @UserId
		END
		ELSE
		BEGIN
			INSERT INTO Patients(UserId,HospitalId,DOB,Gender,RegistrationDate,BloodGroup,Age,ReferedType,GuardianName,GuardianMobile,Relation_with_Patient,Marital,AdmissionDate,DispatchDate,PulseRate,IsAdmited)
			VALUES(@UserId,@HospitalId,@DOB,@Gender,GETDATE(),@BloodGroup,@Age,NULL,@GuardianName,@GuardianMobile,@RelationWithPatient,@MaritalStatus,IIF(@PreferredForAdmit = 1,GETDATE(),NULL),NULL,@PulseRate,@PreferredForAdmit);
			SET @PatientId = SCOPE_IDENTITY();
		END

		INSERT INTO MedicalHistory(PatientId,Diagnosis,Prescription,DoctorId,PrescriptionFile,Wight,DateOfVisit,ReferedType,ReferFrom,ReferedBY)
		VALUES(@PatientId,@Diagnosis,@Prescription,@DoctorId,@PrescriptionFile,@Wight,GETDATE(),@ReferedType,IIF(ISNULL(@ReferedType,'') = '',NULL,@HospitalId),IIF(ISNULL(@ReferedType,'') = '',NULL,@loginId));

		IF(@PreferredForAdmit = 1)
		BEGIN
			INSERT INTO PatientWards(WID,HospitalId,PatientId,WardId,StartOn,EndOn,RecommendedBy,WardTypeId,BedId)
			VALUES(@WID,@HospitalId,@PatientId,@RoomId,GETDATE(),NULL,@loginId,@WardType,@BedId);

			UPDATE HospitalBeds SET Occupied = 1 WHERE Id = @BedId

			INSERT INTO DoctorVisit(WID,HospitalId,DoctorId,VisitTime,VisitDate,EntryAt,PatientId)
			VALUES(@WID,@HospitalId,@DoctorId,CONVERT(VARCHAR, getdate(), 108),CONVERT(VARCHAR, getdate(), 23),GETDATE(),@PatientId);
		END

		COMMIT TRAN
		SELECT 1 Success,'Patient Admit Successfully.' [Message]

	END TRY
	BEGIN CATCH
		ROLLBACK TRAN
		SELECT 0 Success,'Something went wrong try after sometime.' [Message]

	END CATCH
END
