-- Proc_CalculateCommission 30,100,100012,31
CREATE Proc [dbo].[Proc_CalculateCommission]
@UserId int,@PerPatientFee numeric(18,2),@TID int,@HospitalId int
AS
BEGIN
	DECLARE @SuperDistributorComm numeric(18,2),@Distributor numeric(18,2),@Agent numeric(18,2),
			@SuperDistributorId int,@DistributorId int,@AgentId int,@Closing numeric(18,2),@Opening numeric(18,2),
			@Success bit = 0;

    SELECT 
    @SuperDistributorComm = (SELECT Amount  FROM Commission WHERE RoleId = 2),
    @Distributor = (SELECT Amount FROM Commission WHERE RoleId = 3),
    @Agent = (SELECT Amount FROM Commission WHERE RoleId = 4);

	IF EXISTS(select 1 from Users Where Id = @UserId and EntryBy = 1)
	BEGIN
		select @SuperDistributorId = EntryBy,@DistributorId = EntryBy,@AgentId = EntryBy from Users Where Id = @UserId 
	END
	ELSE
	BEGIN
		IF EXISTS(select 1 from Users where Id = @UserId and RoleId = 2)
		BEGIN
			SET @SuperDistributorId = @UserId
		END
		ELSE IF EXISTS(select 1 from Users where Id = @UserId and RoleId = 3)
		BEGIN
			SET @DistributorId = @UserId
			select @SuperDistributorId = EntryBy from Users Where Id = @UserId 
		END
		ELSE IF EXISTS(select 1 from Users where Id = @UserId and RoleId = 4)
		BEGIN
			SET @AgentId = @UserId
			select @DistributorId = EntryBy from Users Where Id = @UserId 
			select @SuperDistributorId = EntryBy from Users Where Id = @DistributorId 
		END

		IF ISNULL(@SuperDistributorId,0) <> 0
		BEGIN
			Update Wallets Set @Opening = Amount,@Closing = Amount = Amount + (@SuperDistributorComm * (@PerPatientFee / 100)) Where UserId = @SuperDistributorId;

			Insert Into [Ledger](UserId,TID,ServiceId,Opening,Amount,Closing,TrType,EntryAt,ReffId)
			Values(@SuperDistributorId,@TID,2,@Opening,(@SuperDistributorComm * (@PerPatientFee / 100)),@Closing,'CR',GETDATE(),@HospitalId);
			SET @Success = 1
		END
		IF ISNULL(@DistributorId,0) <> 0
		BEGIN
			Update Wallets Set @Opening = Amount,@Closing = Amount = Amount + (@Distributor * (@PerPatientFee / 100)) Where UserId = @DistributorId;

			Insert Into [Ledger](UserId,TID,ServiceId,Opening,Amount,Closing,TrType,EntryAt,ReffId)
			Values(@DistributorId,@TID,2,@Opening,(@Distributor * (@PerPatientFee / 100)),@Closing,'CR',GETDATE(),@HospitalId);
			SET @Success = 1
		END
		IF ISNULL(@AgentId,0) <> 0
		BEGIN
			Update Wallets Set @Opening = Amount,@Closing = Amount = Amount + (@Agent * (@PerPatientFee / 100)) Where UserId = @AgentId;

			Insert Into [Ledger](UserId,TID,ServiceId,Opening,Amount,Closing,TrType,EntryAt,ReffId)
			Values(@AgentId,@TID,2,@Opening,(@Agent * (@PerPatientFee / 100)),@Closing,'CR',GETDATE(),@HospitalId);
			SET @Success = 1
		END
	END
END
