@HostName varchar(500) = '',
@Logo varchar(250) = '',
@Banner varchar(250) = '',
@WId int,
@HospitalId int = 0,
@Isdedicated bit,
@Fees numeric(18,2) = 0,
@ShiftId int=0,
@TimeFrom time,
@TimeTo time,
@Qualifications dbo.tp_Qualifications readonly
AS
BEGIN
	Declare @UserId int,@DoctorId int
	IF ISNULL(@HospitalId,0) = 0
	BEGIN
		Select @HospitalId = Id from Hospital where UserId = @EntryBy
	END
	IF @RoleId = 2 AND @EntryBy != 1
	BEGIN
		Select -1 StatusCode,'Access Denied!' [Message]
		return
	END
	
	IF @Id = 0
	BEGIN
		IF Exists(select 1 from Users where MobileNo = @MobileNo)
		begin
			Select -1 StatusCode,'The mobile number provided is already associated with an existing user.' [Message]
			return
		end
		IF @RoleId = 6
		BEGIN
			IF NOT EXISTS(select 1 from @Qualifications)
			BEGIN
				Select -1 StatusCode,'Please Add Qualifications.' [Message]
				return
			END
		END

		Insert into Users(RoleId,FullName,MobileNo,Email,[Password],[Address],CityId,StateId,ZipCode,IsActive,CreatedAt,EntryBy,Tehsil,UserName,Photo,WId,Isdedicated)
		Values(@RoleId,@FullName,@MobileNo,@Email,@Password,@Address,@CityId,@StateId,@ZipCode,1,GETDATE(),@EntryBy,@Tehsil,@UserName,'',@WId,@Isdedicated);
		SET @UserId = SCOPE_IDENTITY();

		IF @RoleId = 5
		BEGIN
			IF @Isdedicated = 1
			Begin
				Insert Into Website(UserId,HostName,EntryAt,Banner,Logo)
				Values(@UserId,@HostName,GETDATE(),@Banner,@Logo);

				SET @WID = SCOPE_IDENTITY();
				Update Users Set WId = @WID where Id = @UserId;
			End
			Insert Into Hospital(UserId,WID,IsDedicated,EntryAt)
			Values(@UserId,@WID,@Isdedicated,GETDATE());
		END

		IF @RoleId = 6
		BEGIN
			Insert Into Doctors(UserId,HospitalId,WID,Fees,EntryAt,ShiftId,TimeFrom,TimeTo)
			Values(@UserId,@HospitalId,@WId,@Fees,GETDATE(),@ShiftId,@TimeFrom,@TimeTo);

			SET @DoctorId = SCOPE_IDENTITY();

			INSERT INTO Qualifications(UserId,[Name],Institution,CompletionYear,EntryAt,DoctorId)
			select @UserId,[Name],Institution,CompletionYear,GETDATE(),@DoctorId from @Qualifications
		END
		IF @RoleId = 7
		BEGIN
			Insert Into Patients(UserId,HospitalId,DOB,Gender,RegistrationDate)
			Values(@UserId,@HospitalId,'','',GETDATE());
		END

		Select 1 StatusCode,'User created successfully.' [Message],@UserName [Data]
	END
	Else
	BEGIN
		Update Users SET FullName = @FullName,MobileNo = @MobileNo,Email = @Email,[Address] = @Address,CityId = @CityId,StateId = @StateId,ZipCode = @ZipCode Where Id = @Id
		Select 1 StatusCode,'User updated successfully.' [Message]
	END
END
