		return
	END
	SELECT 1 StatusCode,'Success.' [Message],u.Id,u.RoleId,u.FullName,u.MobileNo,u.Email,u.[Address],
			u.CityId,u.StateId,u.ZipCode,u.Photo,a.[Role],ISNULL(w.Amount,0) Balance,u.WId,(case when u.RoleId =6 then d.HospitalId else ISNULL(h.Id,0) end) HospitalId,
			ISNULL(d.Id,0) DoctorId
	FROM Users(nolock) u
		Inner Join ApplicationRole a on u.RoleId = a.Id
		Left Join Wallets w on w.UserId = u.Id
		Left Join Hospital h on u.WId = h.WID and (h.UserId = u.Id or h.UserId = u.EntryBy)
		Left Join Doctors d on u.WId = d.WID and d.UserId = u.Id
	WHERE u.UserName = @UserName AND u.[Password] = @Password AND ISNULL(u.IsActive,0) = 1
END
