CREATE Proc [dbo].[Proc_SaveMedicalHistory]
@loginId int,
@PatientId int,
@Diagnosis varchar(500),
@Prescription varchar(1000),
@PrescriptionFile varchar(500),
@Wight varchar(100)
AS
BEGIN
	Declare @PerPatientFee numeric(18,2),@HospitalId int,@HospitalFund numeric(18,2),@Closing numeric(18,2),@TID int,@RoleId int,
			@HospitalAddBy int,@Opening numeric(18,2),@DoctorId int

	Select @HospitalId = EntryBy,@RoleId=RoleId from Users Where Id = @loginId
	Select @PerPatientFee = PerPatientFee from ApplicationSettings
	select @HospitalFund = Amount from Wallets where UserId = @HospitalId
	select @DoctorId = Id from Doctors where UserId = @loginId

	IF @RoleId <> 6
	BEGIN
		Select 'Invalid Access!' [Message]
		return;
	END

	If ISNULL(@HospitalFund,0) < ISNULL(@PerPatientFee,0)
	Begin
		Select 'Insufficient Fund.' [Message]
		return;
	End
	Begin Try
	Begin Tran
		SET @TID = dbo.GenerateCustomTID();

		Update Wallets Set @Closing = Amount = Amount - @PerPatientFee Where UserId = @HospitalId;

		Insert Into [Ledger](UserId,TID,ServiceId,Opening,Amount,Closing,TrType,EntryAt)
		Values(@HospitalId,@TID,2,@HospitalFund,@PerPatientFee,@Closing,'DR',GETDATE());
		
		Insert Into MedicalHistory(PatientId,DoctorId,Diagnosis,Prescription,PrescriptionFile,Wight,DateOfVisit)
		Values(@PatientId,@DoctorId,@Diagnosis,@Prescription,@PrescriptionFile,@Wight,GETDATE());

		Update Wallets Set @Opening = Amount,@Closing = Amount = Amount + @PerPatientFee Where UserId = 1;

		Insert Into [Ledger](UserId,TID,ServiceId,Opening,Amount,Closing,TrType,EntryAt,ReffId)
		Values(1,@TID,2,@Opening,@PerPatientFee,@Closing,'CR',GETDATE(),@HospitalId);

		-- Distribute Commission
		select @HospitalAddBy = EntryBy from Users where Id = @HospitalId
		IF NOT EXISTS(select 1 from Users where Id = @HospitalId And EntryBy = 1)
		BEGIN
			Exec Proc_CalculateCommission @UserId = @HospitalAddBy,@PerPatientFee = @PerPatientFee,@TID = @TID,@HospitalId = @HospitalId
		END

		Commit Tran
		Select 1 Success,'History Created Successfully.' [Message]
	End Try
	Begin Catch
		Rollback Tran
		Insert into DBError(ProcedureName,ErrorMsg,LineNumber,LoginId,EntryAt)
		Values(ERROR_PROCEDURE(),ERROR_MESSAGE(),ERROR_LINE(),@loginId,GETDATE());
		Select 'Something went wrong try after sometime.' [Message]
	End Catch
END
