CREATE Proc [dbo].[Proc_SaveMedicinePurchase]
@LoginId int,
@WID int,
@HospitalId int,
@PharmacyId int,
@SupplierId int,
@InvoiceNo varchar(250),
@PaymentTypeId int,
@PurchaseDate date,
@Details varchar(500),
@Remark varchar(500),
@TotalAmount numeric(18,2),
@PaidAmount numeric(18,2),
@SubTotalAmount numeric(18,2),
@Discount numeric(18,2),
@BalanceAmount numeric(18,2),
@MedicinePurchaseDetails dbo.tp_MedicinePurchaseDetails readonly
AS
BEGIN
	Declare @PurchaseId int,@RowCount int,@MedicineId int,@BatchCode varchar(250),@ExpiryDate date,@BoxPattern int,
			@TotalQty numeric(18,2),@SupplierPrice numeric(18,2),@MRP numeric(18,2),@BalQty numeric(18,2),@Status int

	IF EXISTS(SELECT 1 FROM MedicinePurchase WHERE InvoiceNo = @InvoiceNo)
	BEGIN
		SELECT 'InvoiceNo Alreary Exists' [Message]
		return
	END
	IF NOT EXISTS(SELECT 1 FROM Supplier WHERE ID = @SupplierId)
	BEGIN
		SELECT 'Supplier not found!' [Message]
		return
	END
	IF NOT EXISTS(SELECT 1 FROM MasterPaymentModes WHERE ID = @PaymentTypeId)
	BEGIN
		SELECT 'Invaild payment mode!' [Message]
		return
	END
	IF @PaidAmount > @TotalAmount
	BEGIN
		SELECT 'Paid Amount can not gretter then amount!' [Message]
		return
	END
	IF @TotalAmount = @PaidAmount
	BEGIN
		SET @Status = 1;
	END
	ELSE
	BEGIN
		SET @Status = 4
	END
	BEGIN TRY
		BEGIN TRAN
		Insert Into MedicinePurchase(WID,HospitalId,PharmacyId,SupplierId,InvoiceNo,PurchaseDate,Details,Remark,EntryAt,EntryBy,SubTotalAmount,Discount,TotalAmount,PaidAmount,BalanceAmount,[Status])
		Values(@WID,@HospitalId,@PharmacyId,@SupplierId,@InvoiceNo,@PurchaseDate,@Details,@Remark,GETDATE(),@LoginId,@SubTotalAmount,@Discount,@TotalAmount,@PaidAmount,(@TotalAmount - @PaidAmount),@Status);

		SET @PurchaseId = SCOPE_IDENTITY();

		SELECT * INTO #temp FROM @MedicinePurchaseDetails
		SELECT @RowCount = COUNT(*) FROM #temp
		WHILE @RowCount > 0
		BEGIN

			SELECT @MedicineId = MedicineId,@BatchCode = BatchCode,@ExpiryDate = ExpiryDate,@TotalQty = TotalQuantity,
					@BoxPattern = BoxPattern,@SupplierPrice = SupplierPrice,@MRP = MRP
			FROM #temp ORDER BY MedicineId DESC

			INSERT INTO MedicinePurchaseDetails(WID,HospitalId,PharmacyId,PurchaseId,MedicineId,BatchCode,ExpiryDate,StockQty,BoxPattern,BoxQty,TotalQuantity,SupplierPrice,MRP,TotalPurchasePrice,EntryAt,EntryBy)

			SELECT @WID,@HospitalId,@PharmacyId,@PurchaseId,MedicineId,BatchCode,ExpiryDate,StockQty,BoxPattern,BoxQty,TotalQuantity,SupplierPrice,MRP,TotalPurchasePrice,GETDATE(),@LoginId FROM #temp WHERE MedicineId = @MedicineId;

			SELECT @BalQty = TotalQty FROM MedicineInventory WHERE MedicineId = @MedicineId AND BatchCode = @BatchCode AND ExpiryDate = @ExpiryDate AND BoxPattern = @BoxPattern AND SupplierPrice = @SupplierPrice

			IF EXISTS(SELECT 1 FROM MedicineInventory WHERE MedicineId = @MedicineId AND BatchCode = @BatchCode AND ExpiryDate = @ExpiryDate AND BoxPattern = @BoxPattern AND SupplierPrice = @SupplierPrice)
			BEGIN
				UPDATE MedicineInventory SET TotalQty = TotalQty + @TotalQty WHERE MedicineId = @MedicineId AND BatchCode = @BatchCode AND ExpiryDate = @ExpiryDate AND BoxPattern = @BoxPattern AND SupplierPrice = @SupplierPrice
			END
			ELSE
			BEGIN
				INSERT INTO MedicineInventory(WID,HospitalId,PharmacyId,MedicineId,BatchCode,ExpiryDate,BoxPattern,TotalQty,SupplierPrice,MRP,EntryAt,EntryBy)
				VALUES(@WID,@HospitalId,@PharmacyId,@MedicineId,@BatchCode,@ExpiryDate,@BoxPattern,@TotalQty,@SupplierPrice,@MRP,GETDATE(),@LoginId);
			END

			INSERT INTO MedicineInventoryLedger(WID,HospitalId,PharmacyId,MedicineId,BatchCode,ExpiryDate,BalQty,Qty,ClosingQty,IsOut,EntryAt,EntryBy,BoxPattern,SupplierPrice)
			Values(@WID,@HospitalId,@PharmacyId,@MedicineId,@BatchCode,@ExpiryDate,ISNULL(@BalQty,0),@TotalQty,(ISNULL(@BalQty,0) + @TotalQty),0,GETDATE(),@LoginId,@BoxPattern,@SupplierPrice);

			DELETE FROM #temp WHERE MedicineId = @MedicineId
			SELECT @RowCount = COUNT(*) FROM #temp

		END
		INSERT INTO MedicinePurchasePaymentDetails(WID,HospitalId,PharmacyId,PurchaseId,PaymentModeId,Amount,PaidAmount,BalanceAmount,Remark,EntryAt,EntryBy)
		VALUES(@WID,@HospitalId,@PharmacyId,@PurchaseId,@PaymentTypeId,@TotalAmount,@PaidAmount,(@TotalAmount - @PaidAmount),@Remark,GETDATE(),@LoginId);

		COMMIT TRAN
		SELECT 1 Success,'Record saved successfully!' [Message]
	END TRY
	BEGIN CATCH
		ROLLBACK TRAN
		INSERT INTO DBError(ProcedureName,ErrorMsg,LineNumber,LoginId,EntryAt,ErrorLine)
		VALUES(ERROR_PROCEDURE(),ERROR_MESSAGE(),ERROR_NUMBER(),@LoginId,GETDATE(),ERROR_LINE());
		SELECT 'Something went wrong try after sometime!' [Message]
	END CATCH
END
